import { BaseBlockTracker } from "./BaseBlockTracker";
import { BaseBlockTrackerConfig, BaseBlockTrackerState } from "./IBlockTrackerController";
export interface IPollingBlockTrackerController<T extends {
    idempotencyKey: string;
}, C extends BaseBlockTrackerConfig, S extends BaseBlockTrackerState<T>> extends BaseBlockTracker<T, C, S> {
    /**
     * Checks for the latest block and returns it.
     * This method is typically used to manually trigger a block update
     * and retrieve the most recent block information.
     */
    checkForLatestBlock(): Promise<T>;
}
