'use strict';

var endpoints = require('./endpoints.js');
var pubKeys = require('./pubKeys.js');

const getSapphireNodeDetails = (sapphireNetwork, legacyNetwork, keyType, sigType) => {
  return {
    currentEpoch: "1",
    torusNodeEndpoints: endpoints.getSSSEndpoints(sapphireNetwork, legacyNetwork),
    torusNodeSSSEndpoints: endpoints.getSSSEndpoints(sapphireNetwork, legacyNetwork),
    torusNodeRSSEndpoints: endpoints.getRSSEndpoints(sapphireNetwork, legacyNetwork),
    torusNodeTSSEndpoints: endpoints.getTSSEndpoints(sapphireNetwork, legacyNetwork, keyType, sigType),
    torusIndexes: [1, 2, 3, 4, 5],
    torusNodePub: pubKeys.SAPPHIRE_NODE_PUB_KEYS[sapphireNetwork]
  };
};

exports.getSapphireNodeDetails = getSapphireNodeDetails;
