import { BaseConfig, BaseState } from "../interfaces";
import { BaseCurrencyController } from "./BaseCurrencyController";
export interface BaseCurrencyControllerState extends BaseState {
    currentCurrency: string;
    conversionRate: number;
    conversionDate: string;
    nativeCurrency: string;
    ticker: string;
}
export interface BaseCurrencyControllerConfig extends BaseConfig {
    pollInterval: number;
    api: string;
}
export interface ICurrencyController<C extends BaseCurrencyControllerConfig, S extends BaseCurrencyControllerState> extends BaseCurrencyController<C, S> {
    /**
     * Creates a new poll to periodically call updateConversionRate
     */
    scheduleConversionInterval(): void;
    /**
     * Updates the conversionRate and conversionDate properties associated with the currentCurrency
     */
    updateConversionRate(): void;
}
