'use strict';

var enums = require('../enums.js');

const getTxStatusText = txStatus => {
  switch (txStatus) {
    case "rejected":
    case "unapproved":
    case "failed":
      return enums.ACTIVITY_STATUS_UNSUCCESSFUL;
    case "confirmed":
      return enums.ACTIVITY_STATUS_SUCCESSFUL;
    case "submitted":
      return enums.ACTIVITY_STATUS_PENDING;
    case "cancelled":
      return enums.ACTIVITY_STATUS_CANCELLED;
    default:
      return "";
  }
};

exports.getTxStatusText = getTxStatusText;
